sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        // 1) Build email array from the Flow input
        var emailArray = String(inputs.input_emails || '')
            .split(',')
            .map(function(e){ return e.trim(); })
            .filter(function(e){ return e.length > 0; });

        if (emailArray.length === 0) {
            outputs.status = 'no_emails';
            outputs.message = 'No emails provided';
            return;
        }

        // 2) Call Khoros
        var callRaw = new x_snc_khoros_looku.Khoros().getUserInfoByEmail(emailArray);

        // 3) Normalize result to an array of response objects
        var call;
        if (callRaw && typeof callRaw === 'string') {
            try { call = JSON.parse(callRaw); } catch (e) { call = []; }
        } else {
            call = callRaw;
        }
        if (!Array.isArray(call)) {
            // Some implementations might return a single object
            call = call ? [call] : [];
        }

        // 4) Process each response entry
        call.forEach(function(response) {
            if (!response || response.status !== 'success' || !response.data || !response.data.items || response.data.items.length === 0) {
                gs.warn('Khoros: empty or failed response chunk: ' + JSON.stringify(response));
                return;
            }

            var userData = response.data.items[0];

            var email    = userData.email || '';
            var first    = userData.first_name || '';
            var last     = userData.last_name || '';
            var login    = userData.login || '';
            var idVal    = userData.id || '';
            var ssoId    = userData.sso_id || '';
            var viewHref = userData.view_href || '';

            if (!email) {
                gs.warn('Khoros: userData without email, skipping: ' + JSON.stringify(userData));
                return;
            }

            // 5) Update matching record on x_snc_khoros_looku_khoros_data
            var gr = new GlideRecord('x_snc_khoros_looku_khoros_data');
            gr.addQuery('email', email);
            gr.query();
            if (gr.next()) {
                gr.setValue('first_name', first);
                gr.setValue('last_name',  last);
                gr.setValue('login',      login);
                gr.setValue('id',         idVal);      // field named "id" on this table
                gr.setValue('sso_id',     ssoId);
                gr.setValue('view_href',  viewHref);
                gr.setValue('looked_up',  'true');
                gr.setValue('first_pass', 'true');
                gr.setValue('second_pass','true');
                gr.update();
                gs.info('Updated Khoros data for ' + email);
            } else {
                gs.warn('No matching x_snc_khoros_looku_khoros_data record for ' + email);
            }
        });

        outputs.status = 'ok';
        outputs.message = 'Processed ' + call.length + ' response item(s)';
    } catch (err) {
        outputs.status = 'error';
        outputs.message = err && err.message ? err.message : String(err);
        gs.error('Flow Script error: ' + outputs.message + ' | stack: ' + err.stack);
    }
})(inputs, outputs);

